// rsa 解/加密


function rsaEncrypt($res, $encryptConf, $keyProperty = 'private')
{
    $encryptStr = '';
    $len = strlen($res);
    $num = ceil($len / 117);
    for ($i = 0; $i < $num; $i++)
    {
        $subRes = substr($res, $i * 117, 117);
        if ($keyProperty == 'private') {
            openssl_private_encrypt($subRes, $encrtpt, $encryptConf[$keyProperty]);
        } else {
            openssl_public_encrypt($subRes, $encrtpt, $encryptConf[$keyProperty]);
        }
        $encryptStr .= $encrtpt;
    }
    return base64_encode($encryptStr);
}

function rsaDecrypt($res, $encryptConf, $keyProperty = 'private')
{
    $decryptStr = '';
    $res = base64_decode($res);
    $arr = str_split($res, 128);
    foreach ($arr as $v)
    {
        if ($keyProperty == 'private') {
            openssl_private_decrypt($v, $subStr, $encryptConf[$keyProperty]);
        } else {
            openssl_public_decrypt($v, $subStr, $encryptConf[$keyProperty]);
        }
        $decryptStr .= $subStr;
    }
    return $decryptStr;
}

